query:
mutation AddSession($sessionType: String!, $date: String!, $isMultiSport: Boolean!, $totalDuration: Int, $totalDistance: Float, $weatherTemp: Float, $weatherHumidity: Int, $weatherWindSpeed: Float) {
  createSession(
    input: {sessionType: $sessionType, date: $date, isMultiSport: $isMultiSport, totalDuration: $totalDuration, totalDistance: $totalDistance, weatherTemp: $weatherTemp, weatherHumidity: $weatherHumidity, weatherWindSpeed: $weatherWindSpeed}
  ) {
    id
    sessionType
    date
    isMultiSport
    totalDuration
    totalDistance
    weatherTemp
    weatherHumidity
    weatherWindSpeed
    created_at
    updated_at
    __typename
  }
}
